job.script:



function spooler_process() {

		printInit();

		var listNames =  getParameter().value(LIST_RESULT_FILENAMES).split(";");	
		for (var index in listNames) {					
			 var filename = listNames[index];	
			 //spooler_log.info("##=> Parametros: " + listNames[index]);	
			 if(filename){
				switch (getTypeFile(filename)) {
					case LOAD_FILE_RECEIVED:
						processLoadFileReceived(filename);
						break;
					case LOAD_ISO_FILES:
						processLoadIsoFiles(filename);
						break;
					default:
						break;
				}
			}
		}			
		return true;
	}
